import React from 'react';
import { Button} from '@vkontakte/vkui';
import podcast_icon from '../../resources/icons/podcast_icon.svg'
class UploadPodcast extends React.Component {

    constructor(props){
        super(props)
        this.state = {
            isPickedPodcast : false,
            audioSrc : ""
        }
        this.fileUpload = React.createRef();
        this.showPickedPodcast = this.showPickedPodcast.bind(this)
        this.pickPodcast = this.pickPodcast.bind(this)
        this.fileChangedHandler = this.fileChangedHandler.bind(this);

    }
    showPickedPodcast(){
        this.setState({isPickedPodcast : true})
    }
    pickPodcast(){
        this.fileUpload.current.click();
    }

    fileChangedHandler(event) {
        if(event && event.target.files && event.target.files.length > 0){
            this.showPodcast(event.target.files[0])
        }
    }
    showPodcast = (file) => {
        fr.onload = this.onLoadedFileReader
        fr.readAsDataURL(file);
    }
    onLoadedFileReader = () => {
        img.onload = this.onAudioLoaded
        img.src = fr.result;
    }
    onAudioLoaded = () => {
        this.setState({audioSrc : img.src, isPickedPodcast : true})
        this.props.podcastPicked(img.src)
    }
    render() {
        return (
            <div>
                {!this.state.isPickedPodcast ? <div className="upload_podcast_container">
                    <p className="upload_podcast_title">Загрузите Ваш подкаст</p>
                    <p className="create_donation_text">Выберите готовый аудиофайл из вашего телефона и добавьте его</p>
                    <div onClick={this.pickPodcast} className="stroke_button horizontal-center hover">Загрузить файл</div>
                </div> :
                <div className="podcast_uploaded_container">
                    <div onClick={this.pickPodcast} className="flex just_content hover">
                        <div className="flex">
                            <img className="podcast_icon_picked" src={podcast_icon}/>
                            <p className="podcast_title_picked">My_podcast.mp3</p>
                        </div>
                        <p className="time_podcast_picked">59:16</p>
                    </div>
                    <p className="podcast_picked_decr">Вы можете добавить таймкоды и скорректировать подкаст в режиме редактирования</p>
                    <div className="large_stroke_button hover">Редактировать аудиозапись</div>
                </div>}
                <input id="fileInput" className="custom-file-input hover" type="file" accept="audio/*" onChange={this.fileChangedHandler} style={{ display: "none" }} ref={this.fileUpload}/>

            </div>
        );
    }
}
export default UploadPodcast;
