import React from 'react';
import DayPicker from 'react-day-picker';
import { ActionSheet, ActionSheetItem, CellButton, Panel, View } from '@vkontakte/vkui';
class BottomSheetDialog extends React.Component {
    constructor(props) {
      super(props);

      this.state = {
        popout: null,
        selectedDay: undefined,
      }
      this.handleDayClick = this.handleDayClick.bind(this);
      this.openBase = this.openBase.bind(this);
      this.onClose = this.onClose.bind(this);
    }

    componentDidMount() {
      this.openBase();
    }
    onClose(){
      this.setState({ popout: null })
      this.props.onClose()
    }
    handleDayClick(day) {
        this.setState({ selectedDay: day, popout: null });
        this.props.onDatePicked(day)
    }

    openBase () {

      this.setState({ popout:
        <ActionSheet onClose={this.onClose}>
            <ActionSheetItem  autoclose>
            По дням
            </ActionSheetItem>
            <ActionSheetItem autoclose>
            По неделям
            </ActionSheetItem>
            <ActionSheetItem autoclose>
            По месяцам
            </ActionSheetItem>
            <ActionSheetItem autoclose mode="cancel">Отменить</ActionSheetItem>}
{/*             
            <div onClick={() => this.props.onClose()} className="close_day_picker">Отменить</div> */}
        </ActionSheet>
      });
    }


    render() {
      return (
        <View popout={this.state.popout} activePanel="panel">
            <Panel id="actionSheet">

            </Panel>
        </View>

      )
    }
  }
  export default BottomSheetDialog;
