import React, {
    useCallback,
    useEffect,
    useRef,
    useState,
    useMemo
  } from "react";
  import ReactDOM from "react-dom";
  import styled from "styled-components";
  import { WaveSurfer, WaveForm, Region } from "wavesurfer-react";
  import RegionsPlugin from "wavesurfer.js/dist/plugin/wavesurfer.regions.min";
  import TimelinePlugin from "wavesurfer.js/dist/plugin/wavesurfer.timeline.min";


  function generateNum(min, max) {
    return Math.random() * (max - min + 1) + min;
  }
  const Button = styled.button``;

  /**
   * @param distance
   * @param min
   * @param max
   * @returns {([*, *]|[*, *])|*[]}
   */
  function generateTwoNumsWithDistance(distance, min, max) {
    const num1 = generateNum(min, max);
    const num2 = generateNum(min, max);
    // if num2 - num1 < 10
    if (num2 - num1 >= 10) {
      return [num1, num2];
    }
    return generateTwoNumsWithDistance(distance, min, max);
  }

  export function WaveformExample(props) {
    const [timelineVis, setTimelineVis] = useState(true);

    const plugins = useMemo(() => {
      return [
        {
          plugin: RegionsPlugin,
          options: {

            dragSelection: true,

          }
        },
        timelineVis && {
          plugin: TimelinePlugin,
          options: {
            container: "#timeline",
            height : '26px'
          }
        }
      ].filter(Boolean);
    }, [timelineVis]);

    const toggleTimeline = useCallback(() => {
      setTimelineVis(!timelineVis);
    }, [timelineVis]);

    const [regions, setRegions] = useState([
      {
        id: "region-1",
        start: 0.5,
        end: 10,
        color: "#F2F3F5",
        data: {
          systemRegionId: 31
        }
      }
    ]);

    // use regions ref to pass it inside useCallback
    // so it will use always the most fresh version of regions list
    const regionsRef = useRef(regions);

    useEffect(() => {
      regionsRef.current = regions;
    }, [regions]);

    const regionCreatedHandler = useCallback(
      region => {
        console.log("region-created --> region:", region);

        if (region.data.systemRegionId) return;

        setRegions([
          ...regionsRef.current,
          { ...region, data: { ...region.data, systemRegionId: -1 } }
        ]);
      },
      [regionsRef]
    );

    const wavesurferRef = useRef();
    const handlePosChange = useCallback(position => {
        console.log("position = " + position)
    })
    const handleWSMount = useCallback(
      waveSurfer => {
        wavesurferRef.current = waveSurfer;
        if (wavesurferRef.current) {
            const url = URL.createObjectURL(props.audioSrc)
          wavesurferRef.current.load(url);


          wavesurferRef.current.on("ready", () => {
            console.log("WaveSurfer is ready");
          });

          wavesurferRef.current.on("region-removed", region => {
            console.log("region-removed --> ", region);
          });

          wavesurferRef.current.on("loading", data => {
            console.log("loading --> ", data);
          });
          wavesurferRef.current.on("audioprocess", data => {
            props.onTimeCodeChanged(data)
          });
          wavesurferRef.current.on("interaction", data => {
            props.onTimeCodeChanged(wavesurferRef.current.getCurrentTime())
          });

          if (window) {
            window.surferidze = wavesurferRef.current;
          }
        }

      },
      [regionCreatedHandler]
    );

    const play = useCallback(() => {
      wavesurferRef.current.playPause();
    }, []);
    if (wavesurferRef.current != undefined){
        if (props.isPlaying){
            wavesurferRef.current.play()
        } else {
            wavesurferRef.current.pause()
        }
    }
    const removeAllRegions = useCallback(() => {
        regions.forEach(element => {
            element.pop()
        });    
        setRegions([]);
      }, [regions]);
    if (wavesurferRef.current != undefined){
        if (props.isUndo){
            removeAllRegions()
        }
    }


    return (

      <div className="App">

        <WaveSurfer onPosChange={handlePosChange} plugins={plugins} onMount={handleWSMount}>
          <div className="timeline_bg" id="timeline" />
          <WaveForm 
                    barWidth={2}
                    barRadius={1}
                    barGap={3}
                    cursorWidth={2}
                    height={96} progressColor={"#3F8AE0"} responsive={true} loopSelection={true} waveColor={"#3F8AE0"} cursorColor={"#FF3347"}  id="waveform">

          </WaveForm>
        </WaveSurfer>

      </div>
    );
  }
