import React from 'react';
import { View, Panel, Root, PanelHeader } from '@vkontakte/vkui';

import plus from '../resources/icons/plus.svg'
import { getEpmtyModel } from '../utils/Utils';
import MusicPicker from './MusicPicker';
import Navbar from '../panels/Navbar';
class CreateNew extends React.Component {

    constructor(props){
        super(props)
        this.state = {

        }

    }
    createNewPodcast = () => {
        this.props.setPodcast(getEpmtyModel())
        this.props.changePage("details")
    }

    render() {
        return (
            <div className="main_content">
                <div className="navbar_line"/>
               <div className="create_donation_container">
                   <img className="plus_img" src={plus}/>
                   <p className="create_title">Добавьте первый подкаст</p>
                   <p className="create_donation_text">Добавляйте, редактируйте и делитесь<br/>подкастами вашего сообщества.</p>
                   <div onClick={this.createNewPodcast} className="vk_btn horizontal-center">Добавить подкаст</div>
               </div>
               <View id="music_picker" activePanel="music_picker_panel">
                    <Panel style={{backgroundColor : "white"}} id="music_picker_panel">
                        <PanelHeader>
                            <Navbar back="details" onBack={this.changePage} title="Редактирование"/>
                        </PanelHeader>
                        <MusicPicker setPodcast={this.setPodcast} podcast={this.state.podcast} changePage={this.changePage}/>
                    </Panel>
                </View>
            </div>
        );
    }
}
export default CreateNew;
